package com.cy.bean;public class DrugClassification {    private Integer drugClassificationId;    private String drugClassificationName;    private int drugClassificationState;    public DrugClassification() {    }    public DrugClassification(Integer drugClassificationId, String drugClassificationName, int drugClassificationState) {        this.drugClassificationId = drugClassificationId;        this.drugClassificationName = drugClassificationName;        this.drugClassificationState = drugClassificationState;    }    public Integer getDrugClassificationId() {        return drugClassificationId;    }    public void setDrugClassificationId(Integer drugClassificationId) {        this.drugClassificationId = drugClassificationId;    }    public String getDrugClassificationName() {        return drugClassificationName;    }    public void setDrugClassificationName(String drugClassificationName) {        this.drugClassificationName = drugClassificationName;    }    public int getDrugClassificationState() {        return drugClassificationState;    }    public void setDrugClassificationState(int drugClassificationState) {        this.drugClassificationState = drugClassificationState;    }}