package com.cy.biz.bizImp;import com.cy.bean.*;import com.cy.biz.SpecialService;import com.cy.mapper.SpecialManager;import com.cy.util.StringUtils;import com.github.pagehelper.PageHelper;import com.github.pagehelper.PageInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class SpecialServiceImp implements SpecialService {    @Autowired      private SpecialManager specialManager;    @Override    public PageInfo<DrugStore> selectAllSpecialPageInfo(Map<String, Object> map, String pageNum, int size) {        int page1=0;        if(StringUtils.isEmpty(pageNum)){            page1 = Integer.parseInt(pageNum="1");        }else{            page1 = Integer.parseInt(pageNum);        }        PageHelper.startPage(page1,size);        List<DrugStore> selectSpecialDrugList= specialManager.selectSpecialDrug();        PageInfo<DrugStore> specialDrugPageInfo = new PageInfo<DrugStore>(selectSpecialDrugList);        return specialDrugPageInfo;    }    @Override    public List<DrugClassification> drugClassificationAllStyle() {        return specialManager.drugClassificationAllStyle();    }    @Override    public PageInfo<DrugStore> selectAllSpecialIdPageInfo(Map<String, Object> map, String pageNum, int size, String drugClassificationId) {        int page1=0;        int drugClassificationId1 = 0;        if(StringUtils.isEmpty(pageNum)){            page1 = Integer.parseInt(pageNum="1");        }else{            page1 = Integer.parseInt(pageNum);        }        drugClassificationId1= Integer.parseInt(drugClassificationId);        PageHelper.startPage(page1,size);        List<DrugStore> selectSpecialDrugIdList= specialManager.selectSpecialDrugId(drugClassificationId1);        PageInfo<DrugStore> selectSpecialPageInfo = new PageInfo<DrugStore>(selectSpecialDrugIdList);        return selectSpecialPageInfo;    }}