package com.cy.controller;import com.cy.biz.PhamacyService;import com.cy.util.StringUtils;import com.github.pagehelper.PageInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpSession;import java.util.HashMap;import java.util.Map;@Controller@RequestMapping("/phamacy")public class PhamacyController {    private Map<String,Object> map = new HashMap<>();    @Autowired    private PhamacyService phamacyService;    @RequestMapping("/phamacyAllDrugs.action")    public String  phamacyAllDrugs(HttpServletRequest request, String num , String drugName){        if(!StringUtils.isEmpty(drugName)){            map.put("drugName",drugName);        }        PageInfo  drugpage = phamacyService.selectPhamacyAllDrugsPageInfoPageInfo(map, num,4);                if (drugpage!=null) {            HttpSession session = request.getSession();            session.setAttribute("drugpage",drugpage);            return "/pharmacyPage/phamacyAllDrugs";        } else {            return "error";        }    }    public Map<String, Object> getMap() {        return map;    }    public void setMap(Map<String, Object> map) {        this.map = map;    }    public PhamacyService getPhamacyService() {        return phamacyService;    }    public void setPhamacyService(PhamacyService phamacyService) {        this.phamacyService = phamacyService;    }}    