package com.cy.controller;import com.cy.biz.PhamacyService;import com.cy.util.StringUtils;import com.github.pagehelper.PageInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletRequest;import java.util.HashMap;import java.util.Map;@Controller@RequestMapping("/phamacy")public class PhamacyController {    private Map<String,Object> session = new HashMap<>();    private PhamacyService phamacyService;    @RequestMapping("/phamacyAllDrugs.action")    public String  phamacyAllDrugs(HttpServletRequest request, String num , String drugName){        if(!StringUtils.isEmpty(drugName)){            session.put("drugName",drugName);        }        PageInfo   forpage =   phamacyService.selectPhamacyAllDrugsPageInfoPageInfo(session, num,4);        if (forpage!=null) {            return "phamacyAllDrugs";        } else {            return "error";        }    }    public Map<String, Object> getSession() {        return session;    }    public void setSession(Map<String, Object> session) {        this.session = session;    }    public PhamacyService getPhamacyService() {        return phamacyService;    }    public void setPhamacyService(PhamacyService phamacyService) {        this.phamacyService = phamacyService;    }}    