mapper.select:
select phamacy_secondary_menu_table.phamacySecondName,phamacy_secondary_menu_table.phamacySecondUrl from phamacy_secondary_menu_table
inner join phamacyse_role_table on phamacy_secondary_menu_table.phamacySecondId=phamacyse_role_table.phamacySecondId where
 phamacyse_role_table.adminRoleId=#{adminRoleId}

select phamacy_first_menu_table.phamacyFirstName from phamacy_first_menu_table
inner join phamacy_secondary_menu_table on phamacy_secondary_menu_table.phamacyFirstId=phamacy_first_menu_table.phamacyFirstId where
phamacy_secondary_menu_table.phamacysecondid=#{id}
