package com.cy.util;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import java.io.IOException;import java.io.Reader;public class MyBatisUtil {    private static SqlSessionFactory sqlSessionFactory;    static{        String config = "SqlMapConfig.xml";        Reader reader ;        try {            reader = Resources.getResourceAsReader(config);            SqlSessionFactoryBuilder sqlSessionFactoryBuilder = new SqlSessionFactoryBuilder();            sqlSessionFactory = sqlSessionFactoryBuilder.build(reader);        } catch (IOException e) {            e.printStackTrace();        }    }    public static SqlSession getSession(){        return sqlSessionFactory.openSession();    }    public static void main(String[] args) {        SqlSession session = MyBatisUtil.getSession();        System.out.println(session);        session.close();    }}